    public int findCircleNum(int[][] M) {
        if(null == M) return 0;

        HashMap<Integer,Label> labelMap = new HashMap<>();
        int len = M.length;

        for(int i=0;i<len;i++){
            Label label = new Label(i);
            labelMap.put(i,label);
        }
        
        for(int i=0;i<len;i++){
            Label label1 = labelMap.get(i).getRoot();
            for(int j=i+1;j<len;j++){
                if(1 == M[j][i]){
                    Label label2 = labelMap.get(j).getRoot();
                    label2.Join(label1);
                }
            }
        }
        

        HashSet<Integer> labelCount = new HashSet<>();
        
        for(int i=0;i<len;i++){
            Label label = labelMap.get(i).getRoot();
            labelCount.add(label.getNumber());
        }

        // for(int p:labelCount){
        //     System.out.print(p+" ");
        // }
        // System.out.println();

        return labelCount.size();
    }

    private class Label {
        private Label root;
        private int number;
    
        public Label(int number) {
            this.number = number;
            root = this;
        }
    
        public int getNumber() {
            return number;
        }
    
        public Label getRoot() {
            if (root != this) {
                this.root = root.getRoot();
            }
            return root;
        }
    
        public void Join(Label label) {
            root = label.getRoot();
        }
    }
